mapper.select:
select userid
from member
where userid = #{userid}

select nickname
from member
where nickname = #{nickname}

select email
from member
where email = #{email}

select userid
from member
where email = #{email}

mapper.update:
update member
set pwd = #{pwd},
nickname = #{nickname},
address = #{address},
email = #{email}
where userid = #{userid}

mapper.select:
select city, district
from member
where userid = #{userid}

select stored_file_name, original_file_name
from goods_file
where articleNum = #{articleNum}

mapper.delete:
delete from application_list
 where articleNum = #{articleNum}
   and userid = #{userid}
